	<script src="js/counters.js"></script>
	<script>
		let currentlyPlayingWrapper = null;

		function playCustomVideo(container) {
			const video = container.querySelector('.testimonial-video');
			const overlay = container.querySelector('.video-overlay');

			if (!overlay.classList.contains('video-hidden')) {
				// Pause any currently playing video
				if (currentlyPlayingWrapper && currentlyPlayingWrapper !== container) {
					const prevVideo = currentlyPlayingWrapper.querySelector('.testimonial-video');
					const prevOverlay = currentlyPlayingWrapper.querySelector('.video-overlay');
					if (prevVideo && !prevVideo.paused) {
						prevVideo.pause();
						prevOverlay.classList.remove('video-hidden');
					}
				}

				overlay.classList.add('video-hidden');
				video.play();
				currentlyPlayingWrapper = container;
			}
		}

		function pauseCustomVideo(container) {
			const video = container.querySelector('.testimonial-video');
			const overlay = container.querySelector('.video-overlay');

			if (overlay.classList.contains('video-hidden') && !video.paused) {
				video.pause();
				overlay.classList.remove('video-hidden');
				if (currentlyPlayingWrapper === container) {
					currentlyPlayingWrapper = null;
				}
			}
		}

		document.addEventListener('DOMContentLoaded', function() {
			// Process section slide animation
			const processRow = document.querySelector('.process-row');
			if (processRow) {
				const observer = new IntersectionObserver(function(entries) {
					entries.forEach(function(entry) {
						if (entry.isIntersecting) {
							entry.target.classList.add('animate-in');
							observer.unobserve(entry.target);
						}
					});
				}, {
					threshold: 0.2,
					rootMargin: '0px 0px -100px 0px'
				});
				observer.observe(processRow);
			}

			// Steps grid scale animation
			const stepsGrid = document.querySelector('.steps-grid');
			if (stepsGrid) {
				const stepsObserver = new IntersectionObserver(function(entries) {
					entries.forEach(function(entry) {
						if (entry.isIntersecting) {
							entry.target.classList.add('animate-in');
							stepsObserver.unobserve(entry.target);
						}
					});
				}, {
					threshold: 0.2,
					rootMargin: '0px 0px -100px 0px'
				});
				stepsObserver.observe(stepsGrid);
			}

			const testimonialVideoWrappers = document.querySelectorAll('.testimonial-video-wrapper');
			testimonialVideoWrappers.forEach(function(wrapper) {
				const overlay = wrapper.querySelector('.video-overlay');
				const video = wrapper.querySelector('.testimonial-video');
				
				// Click on overlay to play
				if (overlay) {
					overlay.addEventListener('click', function() {
						playCustomVideo(wrapper);
					});
				}

				// Click on video to pause (when playing)
				if (video) {
					video.addEventListener('click', function(e) {
						// Only pause if video is playing and overlay is hidden
						// Don't pause if clicking on native controls
						if (!video.paused && overlay.classList.contains('video-hidden')) {
							// Check if click was on controls area (bottom 20% of video)
							const rect = video.getBoundingClientRect();
							const clickY = e.clientY - rect.top;
							const controlsArea = rect.height * 0.8; // Top 80% is video area
							
							if (clickY < controlsArea) {
								pauseCustomVideo(wrapper);
							}
						}
					});
				}

				// Reset on video end
				if (video) {
					video.addEventListener('ended', function() {
						const overlay = wrapper.querySelector('.video-overlay');
						overlay.classList.remove('video-hidden');
						video.currentTime = 0;
						video.pause();
						if (currentlyPlayingWrapper === wrapper) {
							currentlyPlayingWrapper = null;
						}
					});
				}
			});
		});

		// Hero Slider Initialization
		(function() {
			const root = document.getElementById('thHeroSlider');
			if (!root) return;

			const viewport = root.querySelector('.thh-viewport');
			const track = root.querySelector('.thh-track');
			if (!viewport || !track) return;

			const slides = Array.from(track.children);
			if (slides.length === 0) return;

			// Clone first slide for infinite loop
			track.appendChild(slides[0].cloneNode(true));

			let index = 0;
			let timer = null;
			let paused = false;
			const css = getComputedStyle(document.documentElement);
			const speed = parseInt(css.getPropertyValue('--th-speed-ms')) || 600;
			const delay = parseInt(css.getPropertyValue('--th-autoplay-ms')) || 2500;

			const getWidth = () => viewport.clientWidth;
			
			const go = (i) => {
				index = i;
				track.style.transition = `transform ${speed}ms var(--th-ease)`;
				track.style.transform = `translateX(${-index * getWidth()}px)`;
			};

			const next = () => {
				if (paused) return;
				go(index + 1);
				if (index > slides.length - 1) {
					setTimeout(() => {
						track.style.transition = 'none';
						index = 0;
						track.style.transform = 'translateX(0)';
					}, speed + 20);
				}
			};

			const start = () => {
				stop();
				timer = setInterval(next, delay);
			};

			const stop = () => {
				if (timer) clearInterval(timer);
			};

			// Pause on hover
			viewport.addEventListener('mouseenter', () => paused = true);
			viewport.addEventListener('mouseleave', () => paused = false);

			// Handle resize
			let resizeTimeout;
			window.addEventListener('resize', () => {
				clearTimeout(resizeTimeout);
				resizeTimeout = setTimeout(() => {
					track.style.transition = 'none';
					track.style.transform = `translateX(${-index * getWidth()}px)`;
				}, 120);
			});

			// Start the slider
			start();
		})();
	</script>
    </body>
</html>

